import * as utils from './utils';
import * as common from './utils/common';

export default {
    ...common,
    ...utils
};

import {
    PackageJsonEditor,
    install,
    uninstall
} from '../utils';
import {allDoExist} from '../utils/common';
/**
 * @type {task[]}
 * @see http://pa11y.org/
 */
export const addA11y = [
    {
        text: 'Add accessibility tasks to package.json',
        task: async ({outputDirectory}) => {
            const scripts = {
                'lint:aria': `pa11y ${outputDirectory}/index.html`
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Install pa11y for checking accessibility rules',
        task: ({skipInstall}) => install(['pa11y'], {dev: true, skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && allDoExist('package.json')
    }
];
export const removeA11y = [
    {
        text: 'Remove accessibility tasks from package.json',
        task: async () => {
            const scripts = {
                'lint:aria': undefined
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Uninstall pa11y',
        task: () => uninstall(['pa11y']),
        condition: ({skipInstall}) => !skipInstall && allDoExist('package.json') && (new PackageJsonEditor()).hasAll('pa11y'),
        optional: ({skipInstall}) => !skipInstall
    }
];
export default addA11y;

import {PackageJsonEditor, install, uninstall} from '../utils';
import {allDoExist, someDoExistSync} from '../utils/common';

const BROWSERSYNC_DEPENDENCIES = [
    'browser-sync',
    'npm-run-all'
];
/**
 * @type {task[]}
 * @see https://www.browsersync.io/docs/command-line
 */
export const addBrowsersync = [
    {
        text: 'Add Browsersync tasks to package.json',
        task: async ({outputDirectory, port}) => {
            const scripts = {
                prestart: 'npm run build:es',
                start: `npm-run-all --parallel watch:es watch:css serve`,
                serve: `browser-sync start --server ${outputDirectory} --files ${outputDirectory} --port ${port}`
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: ({useParcel}) => allDoExist('package.json', 'postcss.config.js') && (someDoExistSync('webpack.config.js', 'rollup.config.js') || useParcel) // eslint-disable-line max-len
    },
    {
        text: 'Install Browsersync dependencies',
        task: ({skipInstall}) => install(BROWSERSYNC_DEPENDENCIES, {dev: true, skipInstall}),
        condition: ({isNotOffline, skipInstall}) => !skipInstall && isNotOffline && allDoExist('package.json')
    }
];
export const removeBrowsersync = [
    {
        text: 'Remove Browsersync tasks from package.json',
        task: async () => {
            const scripts = {
                prestart: undefined,
                start: undefined,
                serve: undefined
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Uninstall Browsersync dependencies',
        task: () => uninstall(BROWSERSYNC_DEPENDENCIES),
        condition: ({skipInstall}) => !skipInstall && allDoExist('package.json') && (new PackageJsonEditor()).hasAll(...BROWSERSYNC_DEPENDENCIES),
        optional: ({skipInstall}) => !skipInstall
    }
];
export default addBrowsersync;

import {join} from 'path';
import {EslintConfigModuleEditor, PackageJsonEditor, install, uninstall} from '../../utils';
import {allDoExist, allDoNotExist} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';
import createJsonEditor from '../../utils/createJsonEditor';

const ALWAYS = () => true;
const CYPRESS_DEPENDENCIES = [
    'cypress',
    'cypress-image-snapshot',
    'cypress-match-screenshot'
];
/**
 * @type {task[]}
 * @see https://www.cypress.io/
 */
export const addCypress = [
    {
        text: 'Create Cypress config file',
        task: async ({port}) => {
            const Editor = createJsonEditor('cypress.json', {
                baseUrl: `http://localhost:${port}`,
                video: false
            });
            await (new Editor())
                .create()
                .commit();
        },
        condition: () => allDoNotExist('cypress.json')
    },
    {
        text: 'Add Cypress test tasks to package.json',
        task: async () => {
            const scripts = {
                'cy:open': 'cypress open',
                'cy:run': 'cypress run',
                'cy:update': 'npm run cy:run -- --env updateSnapshots=true',
                'test:visual': 'npm-run-all --parallel start cy:open',
                'test:visual:update': 'del-cli ./cypress/snapshots',
                'test:visual:ci': 'npm-run-all --parallel start cy:run'
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Add Cypress global variable to .eslintrc.js',
        task: async () => {
            const globals = {cy: true};
            await (new EslintConfigModuleEditor())
                .extend({globals})
                .commit();
        },
        condition: () => allDoExist('.eslintrc.js')
    },
    {
        text: 'Copy Cypress files',
        task: async ({overwrite}) => {
            await (new Scaffolder(join(__dirname, 'templates')))
                .overwrite(overwrite)
                .target('cypress/plugins')
                .copy('plugins/index.js', 'index.js')
                .target('cypress/support')
                .copy('support/index.js', 'index.js')
                .copy('support/commands.js', 'commands.js')
                .target('cypress/integration')
                .copy('visual-regression.test.js')
                .commit();
        },
        condition: ALWAYS
    },
    {
        text: 'Install Cypress dependencies',
        task: ({skipInstall}) => install([...CYPRESS_DEPENDENCIES, 'npm-run-all', 'del-cli'], {dev: true, skipInstall}),
        condition: ({isNotOffline, skipInstall}) => !skipInstall && isNotOffline && allDoExist('package.json')
    }
];
export const removeCypress = [
    {
        text: 'Delete Cypress config file',
        task: async () => {
            const Editor = createJsonEditor('cypress.json');
            await (new Editor())
                .delete()
                .commit();
        },
        condition: () => allDoExist('cypress.json')
    },
    {
        text: 'Remove Cypress test tasks from package.json',
        task: async () => {
            const scripts = {
                'cy:open': undefined,
                'cy:run': undefined,
                'cy:update': undefined,
                'test:visual': undefined,
                'test:visual:update': undefined,
                'test:visual:ci': undefined
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Remove Cypress global variable from .eslintrc.js',
        task: async () => {
            const globals = {cy: false};
            await (new EslintConfigModuleEditor())
                .extend({globals})
                .commit();
        },
        condition: () => allDoExist('.eslintrc.js')
    },
    {
        text: 'Uninstall Cypress dependencies',
        task: () => uninstall(CYPRESS_DEPENDENCIES),
        condition: ({skipInstall}) => !skipInstall && allDoExist('package.json') && (new PackageJsonEditor()).hasAll(...CYPRESS_DEPENDENCIES),
        optional: ({skipInstall}) => !skipInstall
    }
];
export default addCypress;

const {addMatchImageSnapshotPlugin} = require('cypress-image-snapshot/plugin');

module.exports = (on, config) => {
    addMatchImageSnapshotPlugin(on, config);
};

import {addMatchImageSnapshotCommand} from 'cypress-image-snapshot/command';

addMatchImageSnapshotCommand({
    failureThreshold: 0.00,
    failureThresholdType: 'percent',
    customDiffConfig: {threshold: 0.0},
    capture: 'viewport'
});

/// <reference types="Cypress" />

const LONG_ENOUGH = 5000;

describe('Automated Visual Regression Tests', () => {
    beforeEach(() => {
        cy.visit('/');
    });
    it('Should match screenshot', () => {
        const WIDTH = 1200;
        const HEIGHT = 1000;
        cy.viewport(WIDTH, HEIGHT);
        cy.wait(LONG_ENOUGH);
        cy.matchImageSnapshot('homepage');
    });
});

import {join} from 'path';
import {PackageJsonEditor, install} from '../../utils';
import {allDoExist} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';

const DEPENDENCIES = [
    'electron',
    'electron-context-menu',
    'electron-debug',
    'electron-is-dev'
];
const DEV_DEPENDENCIES = [
    'electron-reloader',
    'npm-run-all',
    'spectron'
];
const ALWAYS = () => true;
/**
 * @type {task[]}
 * @see https://electronjs.org/
 */
export const tasks = [
    {
        text: 'Copy electron application files',
        task: async ({overwrite}) => {
            await (new Scaffolder(join(__dirname, 'templates')))
                .overwrite(overwrite)
                .target('.')
                .copy('index.js')
                .target('bin')
                .copy('preload.js')
                .commit();
        },
        condition: ALWAYS
    },
    {
        text: 'Configure metadata and add tasks to package.json',
        task: async ({useParcel}) => {
            const description = `Native Desktop application built with Electron`;
            const main = 'index.js';
            const name = 'tomo-native-app';
            const scripts = {
                'build:electron': 'npm-run-all build:es build:css',
                'prestart:electron': 'npm run build:electron',
                'start:electron': 'electron index --enable-logging',
                dev: `npm-run-all --parallel watch:es${useParcel ? '' : ' watch:css'} start:electron`
            };
            await (new PackageJsonEditor())
                .extend({description, main, name, scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Install electron dependencies',
        task: async ({skipInstall}) => {
            await install(DEPENDENCIES, {skipInstall});
            await install(DEV_DEPENDENCIES, {dev: true, skipInstall});
        },
        condition: ({isNotOffline, skipInstall}) => !skipInstall && isNotOffline && allDoExist('package.json')
    }
];
export default tasks;

/* eslint-env node */
const {join} = require('path');
const {app, BrowserWindow, Menu} = require('electron');
const contextMenu = require('electron-context-menu');
//
// electron-reloaoder - https://github.com/sindresorhus/electron-reloader
// main process file change --> app restart
// renderer file change --> page reload
//
try {require('electron-reloader')(module);} catch (err) {/* intentionally left blank */ }
//
// Add debug features like hotkeys for triggering dev tools and reload
//
require('electron-debug')();

let mainWindow;
const preload = join(__dirname, 'bin', 'preload.js');
const menuItems = [
    {
        label: 'Application',
        submenu: [{
            label: 'Quit',
            accelerator: `${(process.platform === 'darwin') ? 'Command' : 'Control'}+Q`,
            click: () => app.quit()
        }]
    },
    {
        label: 'Debug',
        submenu: [{
            label: 'Toggle DevTools',
            click: () => mainWindow.toggleDevTools()
        }]
    }
];
const applicationMenu = Menu.buildFromTemplate(menuItems);
const createWindow = () => {
    mainWindow = new BrowserWindow({
        width: 800,
        height: 600,
        webPreferences: {preload}
    });
    Menu.setApplicationMenu(applicationMenu);
    contextMenu({prepend: () => menuItems});
    mainWindow.loadURL(`file://${__dirname}/dist/index.html`);
    //
    // Open dev console
    //
    mainWindow.webContents.openDevTools();
    mainWindow.on('closed', () => {
        mainWindow = null;
    });
    mainWindow.webContents.on('devtools-opened', () => {
        mainWindow.focus();
        setImmediate(() => {
            mainWindow.focus();
        });
    });
};
app.on('ready', createWindow);
app.on('window-all-closed', () => {
    //
    // On OS X it is common for applications and their menu bar to stay active until the user quits explicitly with Cmd + Q
    //
    if (process.platform !== 'darwin') {
        app.quit();
    }
});
app.on('activate', () => {
    //
    // On OS X it's common to re-create a window in the app when the dock icon is clicked and there are no other windows open.
    //
    if (mainWindow === null) {
        createWindow();
    }
});

/* eslint-env browser, node */
process.once('loaded', () => {
    window.nodeRequire = require;
    delete window.require;
    delete window.exports;
    delete window.module;
    global.__devtron = {require, process};
    if (process.env.NODE_ENV === 'test') {
        global.electronRequire = require;
    }
});

import {PackageJsonEditor, install} from '../utils';
import {allDoExist, allDoNotExist} from '../utils/common';
import createJsonEditor from '../utils/createJsonEditor';

const ESDOC_CONF = {
    source: './src',
    destination: './docs',
    plugins: [
        {
            name: 'esdoc-standard-plugin'
        },
        {
            name: 'esdoc-ecmascript-proposal-plugin',
            option: {
                all: true
            }
        }
    ]
};
const ESDOC_DEPENDENCIES = [
    'esdoc',
    'esdoc-jsx-plugin',
    'esdoc-ecmascript-proposal-plugin',
    'esdoc-standard-plugin'
];
const ESDOC_REACT_PLUGINS = [
    'esdoc-jsx-plugin'
];
const EsdocJsonEditor = createJsonEditor('esdoc.conf.json', ESDOC_CONF);
/**
 * @type {task[]}
 * @see https://esdoc.org/
 */
export const addEsdoc = [
    {
        text: 'Create esdoc configuration file',
        task: async () => {
            const cfg = new EsdocJsonEditor();
            await cfg.create().commit();
        },
        condition: () => allDoNotExist('esdoc.conf.json', '.esdoc.json')
    },
    {
        text: 'Add documentation tasks to package.json',
        task: async () => {
            const scripts = {
                'lint:docs': `eslint . --no-eslintrc --rule valid-jsdoc:error --parser babel-eslint`,
                predocs: 'npm run lint:docs',
                docs: `esdoc -c esdoc.conf.json`,
                postdocs: 'open-cli ./docs/index.html'
            };
            const pkg = new PackageJsonEditor();
            await pkg.extend({scripts}).commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Install esdoc dependencies',
        task: ({skipInstall}) => install([...ESDOC_DEPENDENCIES, 'open-cli'], {dev: true, skipInstall}),
        condition: ({isNotOffline, skipInstall}) => !skipInstall && isNotOffline && allDoExist('package.json')
    },
    {
        text: 'Install esdoc React plugins',
        task: ({skipInstall}) => install(ESDOC_REACT_PLUGINS, {dev: true, skipInstall}),
        condition: ({isNotOffline, skipInstall, useReact}) => !skipInstall && isNotOffline && useReact && allDoExist('package.json'),
        optional: ({useReact}) => useReact
    },
    {
        text: 'Add esdoc React plugin to configuration file',
        task: async () => {
            const {plugins} = ESDOC_CONF;
            const cfg = new EsdocJsonEditor();
            await cfg
                .extend({
                    plugins: [...plugins, {
                        name: 'esdoc-jsx-plugin',
                        options: {enable: true}
                    }]
                })
                .commit();
        },
        condition: ({useReact}) => useReact && allDoExist('esdoc.conf.json'),
        optional: ({useReact}) => useReact
    }
];
export default addEsdoc;

import {join} from 'path';
import merge from 'lodash.merge';
import {EslintConfigModuleEditor, PackageJsonEditor, install} from '../../utils';
import {allDoExist, allDoNotExist, allDoNotExistSync} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';

const ESLINT_DEPENDENCIES = [
    'eslint',
    'babel-eslint',
    'eslint-config-omaha-prime-grade',
    'watch'
];
const ESLINT_REACT_PLUGINS = [
    'eslint-plugin-react'
];
const ESLINT_SETTINGS = {
    extends: [
        `'omaha-prime-grade'`
    ]
};
const REACT_ESLINT_SETTINGS = merge({}, ESLINT_SETTINGS, {
    parserOptions: {
        ecmaFeatures: {
            jsx: true
        }
    },
    plugins: [`'jsx-a11y'`],
    extends: [
        `'omaha-prime-grade'`,
        `'plugin:react/recommended'`,
        `'plugin:jsx-a11y/recommended'`
    ]
});
/**
 * @type {task[]}
 * @see https://eslint.org/
 */
export const tasks = [
    {
        text: 'Create ESLint configuration and .eslintignore files',
        task: async ({overwrite}) => {
            await (new EslintConfigModuleEditor())
                .create()
                .commit();
            (allDoNotExistSync('.eslintignore') || overwrite) && await (new Scaffolder(join(__dirname, 'templates')))
                .copy('.eslintignore')
                .commit();
        },
        condition: ({overwrite}) => allDoNotExist('.eslintrc.js', '.eslintrc', '.eslintrc.json', '.eslintrc.yml') || overwrite
    },
    {
        text: 'Add lint tasks to package.json',
        task: async ({sourceDirectory}) => {
            const scripts = {
                lint: `eslint . -c ./.eslintrc.js --fix`,
                'lint:ing': `watch 'npm run lint' ${sourceDirectory}`,
                'lint:tests': 'eslint __tests__/**/*.js -c ./.eslintrc.js --fix --no-ignore'
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Install ESLint dependencies',
        task: ({skipInstall}) => install(ESLINT_DEPENDENCIES, {dev: true, skipInstall}),
        condition: ({isNotOffline, skipInstall}) => !skipInstall && isNotOffline && allDoExist('package.json')
    },
    {
        text: 'Install ESLint React plugins',
        task: ({skipInstall}) => install(ESLINT_REACT_PLUGINS, {dev: true, skipInstall}),
        condition: ({isNotOffline, skipInstall, useReact}) => !skipInstall && isNotOffline && useReact && allDoExist('package.json'),
        optional: ({useReact}) => useReact
    },
    {
        text: 'Add lit-html support to ESLint configuration file',
        task: async ({browser, skipInstall}) => {
            const env = {browser};
            const plugins = [`'lit'`];
            await install(['eslint-plugin-lit'], {dev: true, skipInstall});
            await (new EslintConfigModuleEditor())
                .extend(merge({}, ESLINT_SETTINGS, {env, plugins}))
                .extend({extends: [, `'plugin:lit/recommended'`]})
                .commit();
        },
        condition: ({browser, isNotOffline, useReact}) => isNotOffline && browser && !useReact && allDoExist('package.json', '.eslintrc.js'),
        optional: ({browser, useReact}) => browser && !useReact
    },
    {
        text: 'Add React support to ESLint configuration file',
        task: async ({browser, reactVersion, skipInstall}) => {
            const env = {browser};
            const settings = {
                react: {
                    version: `'${reactVersion}'`
                }
            };
            await install(['eslint-plugin-jsx-a11y'], {dev: true, skipInstall});
            await (new EslintConfigModuleEditor())
                .extend(merge({}, REACT_ESLINT_SETTINGS, {env, settings}))
                .commit();
        },
        condition: ({useReact}) => useReact && allDoExist('.eslintrc.js'),
        optional: ({useReact}) => useReact
    }
];
export default tasks;

import {join} from 'path';
import {PackageJsonEditor, install} from '../../utils';
import {allDoExist} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';

const ALWAYS = () => true;
const JEST_DEPENDENCIES = [
    'jest',
    'jest-watch-typeahead',
    'babel-jest'
];
/**
 * @type {task[]}
 * @see https://jestjs.io/
 */
export const addJest = [
    {
        text: 'Add test tasks and Jest configuration to package.json',
        task: async ({browser}) => {
            const scripts = {
                test: 'jest .*.test.js --coverage',
                'test:ing': 'npm test -- --watchAll'
            };
            const jest = {
                testMatch: ['**/__tests__/**/*.(e2e|test).[jt]s?(x)'],
                setupFilesAfterEnv: browser ? ['<rootDir>/__tests__/setup.js'] : undefined,
                watchPlugins: ['jest-watch-typeahead/filename', 'jest-watch-typeahead/testname']
            };
            await (new PackageJsonEditor())
                .extend({jest, scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Copy Jest boilerplate',
        task: async ({browser}) => {
            const scaffolder = new Scaffolder(join(__dirname, 'templates'));
            browser && await scaffolder
                .target('__tests__')
                .copy('setup.js');
            await scaffolder
                .target('__tests__')
                .copy('example.test.js')
                .commit();
        },
        condition: ALWAYS
    },
    {
        text: 'Install Jest dependencies',
        task: ({skipInstall}) => install(JEST_DEPENDENCIES, {dev: true, skipInstall}),
        condition: ({isNotOffline, skipInstall}) => !skipInstall && isNotOffline && allDoExist('package.json')
    }
];
export default addJest;

const delay = duration => new Promise(resolve => {
    setTimeout(resolve, duration);
});

describe('Example', () => {
    test('can pass', () => {
        expect(1).toEqual(1);
        expect(1).not.toEqual(0);
    });
    test('can pass asynchronously', async () => {
        await delay(100);
        expect(1).toEqual(1);
    });
});

class LocalStorageMock {
    constructor() {
        this.store = {};
    }
    clear() {
        this.store = {};
    }
    getItem(key) {
        return this.store[key] || null;
    }
    setItem(key, value) {
        this.store[key] = value.toString();
    }
    removeItem(key) {
        delete this.store[key];
    }
}
global.localStorage = new LocalStorageMock();

import {
    allDoExist,
    allDoExistSync,
    allDoNotExist
} from '../utils/common';
import MakefileEditor from '../utils/MakefileEditor';
/**
 * @type {task[]}
 * @see https://www.gnu.org/software/make/manual/html_node/Simple-Makefile.html#Simple-Makefile
 */
export const addMakefile = [
    {
        text: 'Create Makefile',
        task: async () => {
            await (new MakefileEditor())
                .create()
                .write('# Makefile built with tomo')
                .commit();
        },
        condition: () => allDoNotExist('Makefile')
    },
    {
        text: 'Import tasks from package.json scripts',
        task: async () => {
            await (new MakefileEditor())
                .delete()
                .create()
                .importScripts()
                .appendScripts()
                .appendHelpTask()
                .commit();
        },
        condition: () => allDoExist('Makefile', 'package.json'),
        optional: () => allDoExistSync('Makefile', 'package.json')
    }
];
export default addMakefile;

import {join} from 'path';
import {PackageJsonEditor, install} from '../../utils';
import {allDoExist} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';

const MARIONETTE_DEPENDENCIES = [
    'jquery',
    'backbone',
    'backbone.marionette',
    'backbone.radio',
    'marionette.approuter',
    'lit-html',
    'lodash-es',
    'redux'
];
const ALWAYS = async () => true;
/**
 * @type {task[]}
 * @see https://marionettejs.com/
 */
export const tasks = [
    {
        text: 'Copy Marionette.js boilerplate and assets',
        task: async ({assetsDirectory, overwrite, sourceDirectory, useParcel, usePika}) => {
            const index = (useParcel || usePika) ? 'index-in-place.html' : 'index.html';
            const fonts = useParcel ? 'fonts-in-place.css' : 'fonts.css';
            await (new Scaffolder(join(__dirname, 'templates')))
                .overwrite(overwrite)
                .target(sourceDirectory)
                .copy('main.js')
                .target(`${sourceDirectory}/components`)
                .copy('app.js')
                .copy('header.js')
                .copy('body.js')
                .copy('footer.js')
                .target(`${sourceDirectory}/shims`)
                .copy('mn.renderer.shim.js')
                .target(`${sourceDirectory}/plugins`)
                .copy('mn.radio.logging.js')
                .copy('mn.redux.state.js')
                .commit();
            await (new Scaffolder(join(__dirname, '..', 'common', 'templates')))
                .overwrite(overwrite)
                .target(`${assetsDirectory}`)
                .copy(index, 'index.html')
                .target(`${assetsDirectory}/css`)
