    webpack: addWebpack
};

module.exports = {
    add,
    create,
    new: create// alias for create
};

#!/usr/bin/env node

import React from 'react';
import {render} from 'ink';
import meow from 'meow';
import {cyan, dim, gray} from 'chalk';
import Tomo from './ui';
// import updateNotifier from 'update-notifier';
// Notify updater
// const pkg = require(`../package.json`);
// updateNotifier({pkg}).notify();

const help = `
	${gray.bold('Usage')}
		$ ${cyan('tomo [command] [term] [options]')}

	${gray.bold('Options')}

        --source-directory, -d  Directory for source code [Default: ./src]
        --assets-directory,     Directory for assets [Default: ./assets]
        --use-react, -r         Add React support to workflow [Default: false]
        --react-version         React version for ESLint configuration [Default: '16.8']
		--ignore-warnings, -i   Ignore warning messages [Default: false]
		--skip-install, -s      Skip npm installations [Default: false]

	${gray.bold('Examples')}

		$ tomo
		    ${dim('I love Ink')}
		$ tomo --name=ponies
		    ${dim('I love ponies')}	
`;
const options = {
    help,
    flags: {
        sourceDirectory: {
            type: 'string',
            default: './src',
            alias: 'd'
        },
        assetsDirectory: {
            type: 'string',
            default: './assets'
        },
        useReact: {
            type: 'boolean',
            default: false,
            alias: 'r'
        },
        reactVersion: {
            type: 'string',
            default: '16.8'
        },
        ignoreWarnings: {
            type: 'boolean',
            default: false,
            alias: 'i'
        },
        skipInstall: {
            type: 'boolean',
            default: false,
            alias: 's'
        }
    }
};
const cli = meow(options);

const {input, flags} = cli;
render(<Tomo input={input} flags={flags}/>, {exitOnCtrlC: true});

import React, {useContext, useEffect, useReducer, useState} from 'react';
import PropTypes from 'prop-types';
import {isFunction, isString, isUndefined} from 'lodash';
import Queue from 'p-queue';
import isOnline from 'is-online';
import {Box, Color, Text, StdinContext} from 'ink';
import {default as InkBox} from 'ink-box';
import Spinner from 'ink-spinner';
import SelectInput from 'ink-select-input';
import figures from 'figures';
import commands from './commands';
import {getIntendedInput} from './utils';
const pino = require('pino');

const {assign, entries} = Object;
const space = ' ';
const Check = ({isSkipped}) => <Color bold green={!isSkipped} dim={isSkipped}>{figures.tick}{space}</Color>;
const X = () => <Color bold red>{figures.cross}{space}</Color>;
const Pending = () => <Color cyan><Spinner></Spinner>{space}</Color>;
const Item = ({isSelected, label}) => <Color bold={isSelected} cyan={isSelected}>{label}</Color>;
const Indicator = ({isSelected}) => <Box marginRight={1}>{isSelected ? <Color bold cyan>{figures.arrowRight}</Color> : ' '}</Box>;
const SubCommandSelect = ({items, onSelect}) => <Box paddingTop={1} paddingBottom={1} paddingLeft={1}>
    <SelectInput
        items={items}
        onSelect={onSelect}
        itemComponent={Item}
        indicatorComponent={Indicator}
    ></SelectInput>
</Box>;
const UnderConstruction = () => <Box marginBottom={1}>
    <InkBox padding={{left: 1, right: 1}} margin={{left: 1, top: 1}}>
        <Color bold yellow>UNDER CONSTRUCTION</Color>
    </InkBox>
</Box>;
const ErrorMessage = ({info}) => <Box flexDirection={'column'} marginBottom={1}>
    <InkBox borderColor={'yellow'} margin={{left: 1, top: 1}} padding={{left: 1, right: 1}}>
        <Color yellow>(╯°□ °)╯ ┻━┻ arrrgh...</Color>
    </InkBox>
    <Box marginLeft={4}>
        ↳ <Color dim>Something went wrong...</Color>
    </Box>
    <Box marginLeft={6} marginTop={1}>
        <Color dim><Box>{info}</Box></Color>
    </Box>
</Box>;
class ErrorBoundary extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            info: '',
            error: {},
            hasError: false
        };
    }
    static getDerivedStateFromError() {
        return {hasError: true};
    }
    componentDidCatch(error, info) {
        this.setState({error, info});
    }
    render() {
        const {error, hasError} = this.state;
        const {children} = this.props;
        return hasError ? <ErrorMessage error={error}/> : children;
    }
}
/**
 * Component to display warning message requiring user input
 * @param {Object} props Function component props
 * @param {ReactNode} props.children Function component children
 * @param {function} props.callback Function to be called after user interacts with warning
 * @return {ReactComponent} Warning component
 */
export const Warning = ({callback, children}) => {
    const {setRawMode, stdin} = useContext(StdinContext);
    useEffect(() => {
        setRawMode && setRawMode(true);
        stdin.on('data', callback);
        return function cleanup() {
            stdin.removeListener('data', callback);
            setRawMode && setRawMode(false);
        };
    });
    return <Box flexDirection={'column'} marginBottom={1}>
        <InkBox borderColor={'yellow'} margin={{left: 1, top: 1}} padding={{left: 1, right: 1}}>
            <Color yellow>oops...</Color>
        </InkBox>
        <Box marginLeft={4}>
            ↳ {children}
        </Box>
        <Box marginLeft={6} marginTop={1}>
            <Color dim>Press </Color><Text bold>ENTER</Text><Color dim> to continue</Color>
        </Box>
    </Box>;
};
export const OfflineWarning = () => <Box flexDirection={'column'} marginBottom={1}>
    <InkBox borderColor={'yellow'} margin={{left: 1, top: 1}} padding={{left: 1, right: 1}}>
        <Color yellow>(⌒_⌒;) This is awkward...</Color>
    </InkBox>
    <Box marginLeft={4} flexDirection={'column'}>
        <Box>↳ <Text>...but you appear to be <Color bold red>offline</Color></Text></Box>
        <Box>↳ <Text>Please connect to the internet and <Color bold cyan>try again</Color></Text></Box>
    </Box>
    <Box marginLeft={6} marginTop={1}>
        <Color dim>No dependencies were installed</Color>
    </Box>
</Box>;
export const CommandError = errors => {
    const log = pino(
        {prettyPrint: {levelFirst: true}},
        pino.destination('./tomo-errors.txt')
    );
    useEffect(() => {
        log.error(errors);
    }, []);
    return <Box flexDirection={'column'} marginTop={1} marginLeft={1}>
        <Box><X/><Text>Something has gone horribly <Color bold red>wrong</Color></Text></Box>
        <Box marginLeft={2}>↳{space}<Color dim>Details written to ./tomo-errors.txt</Color></Box>
    </Box>;
};
/**
 * Add async tasks to a queue, handle completion with actions dispatched via dispatch function
 * @param {Object} data Data to be used for populating queue
 * @param {Queue} [data.queue={}] p-queue instance
 * @param {Object[]} [data.tasks=[]] Array of task objects
 * @param {function} [data.dispatch=()=>{}] Function to dispatch task completion (complete, skip, error) actions
 * @param {Object} [data.options={}] Options object to pass to task function
 * @return {undefined} Returns nothing (side effects only)
 */
export async function populateQueue(data = {queue: {}, tasks: [], dispatch: () => {}, options: {skipInstall: false}}) {
    const {queue, tasks, dispatch, options} = data;
    const {skipInstall} = options;
    const isNotOffline = skipInstall || await isOnline();
    dispatch({type: 'status', payload: isNotOffline ? 'online' : 'offline'});
    for (const [index, item] of tasks.entries()) {
        const {condition, task} = item;
        try {
            if (await condition({...options, isNotOffline})) {
                await queue
                    .add(() => task({...options, isNotOffline}))
                    .then(() => dispatch({type: 'complete', payload: index}))
                    .catch(() => dispatch({
                        type: 'error', payload: {
                            index,
                            title: 'Failed to add task to queue',
                            location: 'task',
                            details: item.text
                        }
                    }));
            } else {
                dispatch({type: 'skipped', payload: index});
            }
        } catch (error) {
            dispatch({
                type: 'error',
                payload: {
                    error,
                    index,
                    title: 'Failed to test task conditions',
                    location: 'condition',
                    details: item.text
                }
            });
        }
    }
}
/**
 * Task component
 * @param {Object} props Function component props
 * @param {boolean} props.isComplete Control display of check (true) or loading (false)
 * @param {boolean} props.isErrored Control display of x (true)
 * @param {boolean} props.isSkipped Control color of check - green (false) or dim (true)
 * @param {string} props.text Task text
 * @example
 * <Task text={'This task is done before it starts'} isComplete={true}></Task>
 * @return {ReactComponent} Task component
 */
export const Task = ({isComplete, isErrored, isPending, isSkipped, text}) => <Box flexDirection='row' marginLeft={3}>
    {isComplete && <Check isSkipped={isSkipped}></Check>}
    {isErrored && <X/>}
    {isPending && <Pending/>}
    <Text><Color dim={isComplete}>{text}</Color></Text>
</Box>;
/**
 * Task list component
 * @param {Object} props Function component props
 * @param {string} props.command Command - new | create | add
 * @param {Object} props.options Command line flags (see help)
 * @param {string[]} props.terms Terms - eslint | babel | jest | postcss | docs
 * @example
 * <TaskList command={'add'} terms={'eslint'} options={{skipInstall: true}}></TaskList>
 * @return {ReactComponent} Task list component
 */
export const TaskList = ({command, options, terms, done}) => {
    const dict = val => new Map(entries(val));
    const reducer = (state, {type, payload}) => {
        const {completed, errors, skipped} = state;
        const update = val => assign({}, state, val);
        const lookup = dict({
            complete: () => update({completed: [...completed, payload]}),
            skipped: () => update({skipped: [...skipped, payload]}),
            error: () => update({errors: [...errors, {payload}]}),
            status: () => update({status: payload})
        });
        return lookup.get(type)();
    };
    const initialState = {
        completed: [],
        skipped: [],
        errors: []
    };
    const [state, dispatch] = useReducer(reducer, initialState);
    const {completed, errors, skipped, status} = state;
    const queue = new Queue({concurrency: 1});
    const tasks = commands[command][terms[0]];
    const tasksComplete = ((completed.length + skipped.length) === tasks.length);
    const hasError = (errors.length > 0);
    const {skipInstall} = options;
    useEffect(() => {
        populateQueue({queue, tasks, options, dispatch});
    }, [tasks]);
    tasksComplete && isFunction(done) && done();
    return <ErrorBoundary>
        {status === 'offline' && !skipInstall && <OfflineWarning/>}
        {hasError && <CommandError errors={errors}></CommandError>}
        <Box flexDirection={'column'} marginBottom={1}>
            <InkBox
                margin={{left: 1, top: 1}}
                padding={{left: 1, right: 1}}
                borderColor={tasksComplete ? 'green' : (hasError ? 'red' : 'cyan')}
                borderStyle={'round'}>
                <Color bold white>{command} {terms.join(' ')}</Color>
            </InkBox>
            <Box flexDirection='column' marginBottom={1}>
                {tasks.map(({optional, text}, index) => {
                    const {completed, errors, skipped} = state;
                    const isSkipped = skipped.includes(index);
                    const isComplete = completed.includes(index) || isSkipped;
                    const isErrored = errors.map(error => error.payload.index).includes(index);
                    const isPending = [isComplete, isSkipped, isErrored].every(val => !val);
                    const shouldBeShown = isUndefined(optional) || (isFunction(optional) && optional(options));
                    return shouldBeShown ?
                        <Task
                            text={text}
                            isSkipped={isSkipped}
                            isComplete={isComplete}
                            isErrored={isErrored}
                            isPending={isPending}
                            key={index}>
                        </Task> :
                        <Box key={index}></Box>;
                })}
            </Box>
        </Box>
    </ErrorBoundary>;
};
/**
 * Main tomo UI component
 * @param {Object} props Component props
 * @return {ReactComponent} Main tomo UI component
 */
const UI = props => {
    const {done, flags, input} = props;
    const {ignoreWarnings} = flags;
    const [command, ...terms] = input;
    const hasCommand = isString(command);
    const intended = hasCommand ? getIntendedInput(commands, command, terms) : [, []];
    const {intendedCommand} = intended;
    const [hasTerms, setHasTerms] = useState(terms.length > 0);
    const [intendedTerms, setIntendedTerms] = useState(intended.intendedTerms);
    const compare = (term, index) => (term !== terms[index]);
    const [showWarning, setShowWarning] = useState(((command !== intendedCommand) || intendedTerms.map(compare).some(Boolean)) && !ignoreWarnings);
    const VALID_COMMANDS = hasCommand ? Object.keys(commands[intendedCommand]) : [];
    const selectInputCommandItems = hasCommand ? VALID_COMMANDS.map(command => ({label: command, value: command})) : [];
    const updateWarning = data => {
        const key = String(data);
        (key === '\r') ? setShowWarning(false) : process.exit(0);
    };
    const updateTerms = ({value}) => {
        setHasTerms(true);
        setIntendedTerms([value]);
    };
    return <ErrorBoundary>
        {showWarning ?
            <Warning callback={updateWarning}>
                <Text>Did you mean <Color bold green>{intendedCommand} {intendedTerms.join(' ')}</Color>?</Text>
            </Warning> :
            (hasCommand && hasTerms) ?
                <TaskList command={intendedCommand} terms={intendedTerms} options={flags} done={done}></TaskList> :
                hasCommand ?
                    <SubCommandSelect items={selectInputCommandItems} onSelect={updateTerms}></SubCommandSelect> :
                    <UnderConstruction />
        }
    </ErrorBoundary>;
};
Check.propTypes = {
    isSkipped: PropTypes.bool
};
Check.defaultProps = {
    isSkipped: false
};
SubCommandSelect.propTypes = {
    items: PropTypes.arrayOf(PropTypes.object),
    onSelect: PropTypes.func
};
Indicator.propTypes = {
    isSelected: PropTypes.bool
};
Indicator.defaultProps = {
    isSelected: false
};
Item.propTypes = {
    isSelected: PropTypes.bool,
    label: PropTypes.string.isRequired
};
Item.defaultProps = {
    isSelected: false
};
ErrorMessage.propTypes = {
    info: PropTypes.string
};
ErrorBoundary.propTypes = {
    children: PropTypes.node
};
Task.propTypes = {
    isComplete: PropTypes.bool,
    isErrored: PropTypes.bool,
    isSkipped: PropTypes.bool,
    isPending: PropTypes.bool,
    text: PropTypes.string
};
Task.defaultProps = {
    isComplete: false,
    isErrored: false,
    isSkipped: false,
    isPending: false,
    text: 'task description'
};
TaskList.propTypes = {
    command: PropTypes.string,
    options: PropTypes.any,
    terms: PropTypes.arrayOf(PropTypes.string),
    done: PropTypes.func
};
TaskList.defaultProps = {
    command: '',
    options: {skipInstall: false},
    terms: []
};
Warning.propTypes = {
    callback: PropTypes.func,
    children: PropTypes.node
};
UI.propTypes = {
    input: PropTypes.array,
    flags: PropTypes.object,
    done: PropTypes.func
};
UI.defaultProps = {
    input: [],
    flags: {}
};

export default UI;

import {join} from 'path';
import Queue from 'p-queue';
import memFs from 'mem-fs';
import editor from 'mem-fs-editor';

const {assign} = Object;
/**
 * Base class to serve as base for JSON and module builder classes
 */
export class BasicEditor {
    constructor() {
        const fs = editor.create(memFs.create());
        const queue = new Queue({concurrency: 1});
        assign(this, {fs, queue});
    }
    /**
     *
     * @param {string} destination Destination to copy file
     * @return {BasicEditor} Chaining OK
     */
    copy(destination) {
        const self = this;
        const {fs, path, queue} = self;
        const [filename] = path.split('/').reverse();
        queue.add(() => fs.copy(path, join(destination, filename)));
        return self;
    }
    /**
     * @return {BasicEditor} Chaining OK
     */
    delete() {
        const self = this;
        const {fs, path, queue} = self;
        queue.add(() => fs.delete(path));
        return self;
    }
    done() {
        return this.queue.onEmpty();
    }
    /**
     * Write changes to disk
     * @return {Promise} Resolves when queue is empty
     */
    async commit() {
        const {fs} = this;
        await new Promise(resolve => fs.commit(resolve));
        await this.done();
    }
}
export default BasicEditor;

import {flow, kebabCase, last, negate} from 'lodash';
import {existsSync} from 'fs-extra';
import createJsonEditor from './createJsonEditor';
import createModuleEditor from './createModuleEditor';
import {getBinDirectory, parse} from './common';

const {assign, entries} = Object;
const {isArray} = Array;
const isNotArray = negate(isArray);
const silent = () => {};
const isLocalNpmCommand = (command, path = process.cwd()) => {
    const [packageDirectory] = path.split('Makefile');
    const Editor = createJsonEditor('package.json');
    const pkg = new Editor(packageDirectory);
    const pkgHasCommmand = pkg.hasAll(command);
    const binHasCommand = existsSync(`${getBinDirectory(path)}${command}`);
    return pkgHasCommmand || binHasCommand;
};
/**
 * Create and edit Makefiles. Includes ability to import package.json scripts.
 */
export class MakefileEditor extends createModuleEditor('Makefile') {
    contents = '';
    scripts = {};
    useBinVariable = false;
    constructor(path = process.cwd()) {
        super(path);
    }
    write(contents) {
        const self = this;
        const {fs, path, queue} = self;
        queue
            .add(() => fs.write(path, contents))
            .then(() => self.created = existsSync(path))
            .catch(silent);
        return assign(self, {contents});
    }
    /**
     * Append line(s) to end of Makefile
     * @param {(string|Symbol)} [lines=Symbol('skip')] Lines to append
     * @return {MakefileEditor} Chaining OK
     */
    append(lines = Symbol('skip')) {
        const {contents} = this;
        const shouldSkip = (typeof lines === 'symbol');
        return shouldSkip ? this : this.write(`${contents}\n${lines}`);
    }
    /**
     * Prepend line(s) to top of Makefile (similar API to {@link MakefileEditor#append})
     * @param {(string|Symbol)} [lines=Symbol('skip')] Lines to append
     * @return {MakefileEditor} Chaining OK
     */
    prepend(lines = Symbol('skip')) {
        const {contents} = this;
        const shouldSkip = (typeof lines === 'symbol');
        return shouldSkip ? this : this.write(`${lines}\n${contents}`);
    }
    /**
     * Format task and remove package.json dependency by replacing npm with make and such
     * @param {string} action Task action
     * @param {object} scripts Scripts object imported from package.json (must run {@link MakefileEditor#importScripts} first)
     * @return {string} Formatted action
     */
    formatTask(action, scripts = {}) {
        const {path} = this;
        const formatTaskName = val => kebabCase(last(val.split(' ')));
        const replaceNpmRunQuotes = initial => {
            const re = /['"]npm run .[^"]*['"]/g;
            const matches = action.match(re);
            return isNotArray(matches) ? initial : matches.reduce((acc, match) => acc.replace(match, `'make ${formatTaskName(match)}'`), initial);
        };
        const replaceNpmWithArguments = initial => {
            const re = /npm .* -- --.*/g;
            const matches = action.match(re);
            return isNotArray(matches) ? initial : matches.reduce((acc, match) => {
                const [commands, options] = match.split(' -- ');
                const task = last(commands.split(' '));
                return acc.replace(match, `${scripts[task]} ${options}`);
            }, initial);
        };
        const replaceNpmRunCommands = initial => {
            const re = /^npm run .*/g;
            const matches = action.match(re);
            return isNotArray(matches) ? initial : matches.reduce((acc, match) => acc.replace(match, `$(MAKE) ${formatTaskName(match)}`), initial);
        };
        const formatted = flow(
            replaceNpmRunQuotes,
            replaceNpmWithArguments,
            replaceNpmRunCommands
        )(action);
        const [command] = formatted.split(' ');
        const useBinVariable = isLocalNpmCommand(command, path);
        this.useBinVariable = this.useBinVariable || useBinVariable;
        return `${useBinVariable ? `$(bin)` : ''}${formatted}`;
    }
    /**
     * Add task to Makefile (appended to end)
     * @param {string} name Task name ("build", "lint", etc...)
     * @param {string[]} tasks Lines of code to be executed during task
     * @param {object} options Configure task
     * @param {string} [options.description] Task description used in help task
     * @param {boolean} [options.silent=false] Prepend "@" (true) or not (false)
     * @return {MakefileEditor} Chaining OK
     */
    addTask(name, tasks, options = {description: 'Task description'}) {
        const self = this;
        const {scripts} = self;
        const {description} = options;
        return tasks.reduce((tasks, action) => tasks
            .append(`\t${self.formatTask(action, scripts)}`)
            .addTaskDescription(name, description)
        , self.append(`${name}:`));
    }
    /**
     * Add deescription to task for use during help task
     * @param {string} task Task name
     * @param {string} description Description text
     * @example
     * const makefile = await (new MakefileEditor())
     *     .addTask('foo', 'echo foo')
     *     .commit();
     * // foo: ## Task description <-- default task description
     * //     echo foo
     * //
     * await makefile
     *     .addTaskDescription('foo', 'This task does foo')
     *     .commit();
     * // foo: ## This task does foo
     * //     echo foo
     * //
     * @return {MakefileEditor} Chaining OK
     */
    addTaskDescription(task, description = 'Task description') {
        const contents = this.contents.replace(`${kebabCase(task)}:\n`, `${kebabCase(task)}: ## ${description}\n`);
        return this.write(contents);
    }
    appendHelpTask() {
        const task = `@fgrep -h "##" $(MAKEFILE_LIST) | fgrep -v fgrep | sed -e 's/\\$$//' | sed -e 's/##/\\n    /'`;
        return this.addTask('help', [task], {description: 'Show this help'});
    }
    /**
     * Add Makefile comment
     * @param {string} text Comment text
     * @return {MakefileEditor} Chaining OK
     */
    addComment(text) {
        return this.append(`# ${text}`);
    }
    importScripts() {
        const {path} = this;
        const [packageDirectory] = path.split('Makefile');
        const Editor = createJsonEditor('package.json');
        const pkg = (new Editor(packageDirectory)).read();
        const {scripts} = parse(pkg);
        return assign(this, {scripts});
    }
    /**
     * Append tasks imported from package.json
     * @example <caption>Must execute importScripts first</caption>
     * const makefile = await (new MakefileEditor())
     *     .importScripts()
     *     .appendScripts()
     *     .commit();
     * @return {MakefileEditor} Chaining OK
     */
    appendScripts() {
        const self = this;
        const {path, scripts} = self;
        const tasks = entries(scripts).map(([key, value]) => [kebabCase(key), [value]]);
        const getPreTask = (tasks, name) => {
            const [data] = tasks
                .filter(([name]) => name.startsWith('pre'))
                .map(([name, values]) => [name.substring('pre'.length), values])
                .filter(task => task[0] === name);
            return isArray(data) ? data[1] : [];
        };
        const getPostTask = (tasks, name) => {
            const [data] = tasks
                .filter(([name]) => name.startsWith('post'))
                .map(([name, values]) => [name.substring('post'.length), values])
                .filter(task => task[0] === name);
            return isArray(data) ? data[1] : [];
        };
        return tasks
            .filter(([name]) => !(name.startsWith('pre') || name.startsWith('post')))
            .map(([name, values]) => [name, [...getPreTask(tasks, name), ...values, ...getPostTask(tasks, name)]])
            .reduce((tasks, [key, values]) => tasks.addTask(key, values).append(''), self.append(''))
            .prepend(self.useBinVariable ? `bin := ${getBinDirectory(path)}` : Symbol('skip'))
            .prepend(`# Built from ${path}/package.json`);
    }
}
export default MakefileEditor;

