import {
    PackageJsonEditor,
    install,
    uninstall
} from '../utils';
import {allDoExist} from '../utils/common';
/**
 * @type {task[]}
 * @see http://pa11y.org/
 */
export const addA11y = [
    {
        text: 'Add pa11y tasks to package.json',
        task: async ({outputDirectory}) => {
            const scripts = {
                'lint:a11y': `pa11y ${outputDirectory}/index.html`
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Install pa11y',
        task: ({skipInstall}) => install(['pa11y'], {dev: true, skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && allDoExist('package.json')
    }
];
export const removeA11y = [
    {
        text: 'Remove pa11y tasks from package.json',
        task: async () => {
            const scripts = {
                'lint:a11y': undefined
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Uninstall pa11y',
        task: () => uninstall(['pa11y']),
        condition: ({skipInstall}) => !skipInstall && allDoExist('package.json') && (new PackageJsonEditor()).hasAll('pa11y'),
        optional: ({skipInstall}) => !skipInstall
    }
];
export default addA11y;

import {BabelConfigModuleEditor, PackageJsonEditor, install} from '../utils';
import {allDoNotExist, someDoExist} from '../utils/common';

const BABEL_CORE = [
    '@babel/cli',
    '@babel/core',
    '@babel/runtime'
];
const BABEL_PRESETS = [
    '@babel/preset-env',
    'babel-preset-minify'
];
const BABEL_PLUGINS = [
    '@babel/plugin-transform-runtime',
    '@babel/plugin-proposal-class-properties',
    '@babel/plugin-proposal-export-default-from',
    '@babel/plugin-proposal-optional-chaining'
];
const BABEL_REACT_PRESET = [
    '@babel/preset-react'
];
const BABEL_DEPENDENCIES = [
    ...BABEL_CORE,
    ...BABEL_PRESETS,
    ...BABEL_PLUGINS
];
/**
 * @type {task[]}
 * @see https://babeljs.io/
 */
export const addBabel = [
    {
        text: 'Create Babel config file',
        task: async () => {
            await (new BabelConfigModuleEditor())
                .create()
                .commit();
        },
        condition: () => allDoNotExist('babel.config.js', '.babelrc', '.babelrc.js')
    },
    {
        text: 'Add Babel build task to package.json',
        task: async ({outputDirectory, sourceDirectory}) => {
            const scripts = {
                build: `babel ${sourceDirectory} --out-dir ${outputDirectory}`,
                'build:watch': `watch 'npm run build' ${sourceDirectory}`
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();

        },
        condition: () => someDoExist('package.json')
    },
    {
        text: 'Install Babel core, CLI, presets, and plugins',
        task: ({skipInstall}) => install(BABEL_DEPENDENCIES, {dev: true, skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && (!(new PackageJsonEditor()).hasAll(...BABEL_DEPENDENCIES) && someDoExist('package.json'))
    },
    {
        text: 'Install Babel React preset',
        task: ({skipInstall}) => install(BABEL_REACT_PRESET, {dev: true, skipInstall}),
        condition: ({useReact}) => (useReact && someDoExist('package.json')),
        optional: ({isNotOffline, useReact}) => isNotOffline && useReact
    },
    {
        text: 'Add React support to Babel configuration file',
        task: async () => {
            const presets = [...BABEL_PRESETS, ...BABEL_REACT_PRESET];
            await (new BabelConfigModuleEditor())
                .extend({presets})
                .commit();
        },
        condition: ({useReact}) => useReact && someDoExist('babel.config.js'),
        optional: ({useReact}) => useReact
    }
];
export default addBabel;

import {
    PackageJsonEditor,
    install,
    uninstall
} from '../utils';
import {allDoExist, someDoExist, someDoExistSync} from '../utils/common';

const BROWSERSYNC_DEPENDENCIES = [
    'browser-sync',
    'npm-run-all'
];
/**
 * @type {task[]}
 * @see https://www.browsersync.io/docs/command-line
 */
export const addBrowsersync = [
    {
        text: 'Add Browsersync tasks to package.json',
        task: async ({outputDirectory}) => {
            const scripts = {
                prestart: 'npm run build',
                start: 'npm-run-all --parallel build:watch build:css:watch serve',
                serve: `browser-sync start --server ${outputDirectory} --files ${outputDirectory}`
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: ({useParcel}) => allDoExist('package.json', 'postcss.config.js') && (someDoExistSync('webpack.config.js', 'rollup.config.js') || useParcel) // eslint-disable-line max-len
    },
    {
        text: 'Install Browsersync dependencies',
        task: ({skipInstall}) => install(BROWSERSYNC_DEPENDENCIES, {dev: true, skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && someDoExist('package.json')
    }
];
export const removeBrowsersync = [
    {
        text: 'Remove Browsersync tasks from package.json',
        task: async () => {
            const scripts = {
                prestart: undefined,
                start: undefined,
                serve: undefined
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Uninstall Browsersync dependencies',
        task: () => uninstall(BROWSERSYNC_DEPENDENCIES),
        condition: ({skipInstall}) => !skipInstall && someDoExist('package.json') && (new PackageJsonEditor()).hasAll(...BROWSERSYNC_DEPENDENCIES),
        optional: ({skipInstall}) => !skipInstall
    }
];
export default addBrowsersync;

import {join} from 'path';
import {PackageJsonEditor, install} from '../../utils';
import {allDoExist} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';

const DEPENDENCIES = [
    'electron',
    'electron-context-menu',
    'electron-debug',
    'electron-is-dev'
];
const DEV_DEPENDENCIES = [
    'electron-reloader',
    'npm-run-all',
    'spectron'
];
const ALWAYS = () => true;
/**
 * @type {task[]}
 * @see https://electronjs.org/
 */
export const tasks = [
    {
        text: 'Copy electron application files',
        task: async () => {
            await (new Scaffolder(join(__dirname, 'templates')))
                .target('.')
                .copy('index.js')
                .target('bin')
                .copy('preload.js')
                .commit();
        },
        condition: ALWAYS
    },
    {
        text: 'Configure metadata and add tasks to package.json',
        task: async ({useParcel}) => {
            const description = `Native Desktop application built with Electron`;
            const main = 'index.js';
            const name = 'tomo-native-app';
            const scripts = {
                'electron:build': 'npm-run-all build build:css',
                'preelectron:start': 'npm run electron:build',
                'electron:start': 'electron index',
                'electron:dev': 'npm run electron:start -- --enable-logging',
                dev: `npm-run-all${useParcel ? '' : ' electron:build'} --parallel build:watch${useParcel ? '' : ' build:css:watch'} electron:dev`
            };
            await (new PackageJsonEditor())
                .extend({description, main, name, scripts})
                .commit();
        },
        condition: () => allDoExist('package.json')
    },
    {
        text: 'Install electron dependencies',
        task: async ({skipInstall}) => {
            await install(DEPENDENCIES, {skipInstall});
            await install(DEV_DEPENDENCIES, {dev: true, skipInstall});
        },
        condition: () => allDoExist('package.json')
    }
];
export default tasks;

/* eslint-env node */
const {join} = require('path');
const {app, BrowserWindow, Menu} = require('electron');
const contextMenu = require('electron-context-menu');
//
// electron-reloaoder - https://github.com/sindresorhus/electron-reloader
// main process file change --> app restart
// renderer file change --> page reload
//
try {require('electron-reloader')(module);} catch (err) {/* intentionally left blank */ }
//
// Add debug features like hotkeys for triggering dev tools and reload
//
require('electron-debug')();

let mainWindow;
const preload = join(__dirname, 'bin', 'preload.js');
const menuItems = [
    {
        label: 'Application',
        submenu: [{
            label: 'Quit',
            accelerator: `${(process.platform === 'darwin') ? 'Command' : 'Control'}+Q`,
            click: () => app.quit()
        }]
    },
    {
        label: 'Debug',
        submenu: [{
            label: 'Toggle DevTools',
            click: () => mainWindow.toggleDevTools()
        }]
    }
];
const applicationMenu = Menu.buildFromTemplate(menuItems);
const createWindow = () => {
    mainWindow = new BrowserWindow({
        width: 800,
        height: 600,
        webPreferences: {preload}
    });
    Menu.setApplicationMenu(applicationMenu);
    contextMenu({prepend: () => menuItems});
    mainWindow.loadURL(`file://${__dirname}/dist/index.html`);
    //
    // Open dev console
    //
    mainWindow.webContents.openDevTools();
    mainWindow.on('closed', () => {
        mainWindow = null;
    });
    mainWindow.webContents.on('devtools-opened', () => {
        mainWindow.focus();
        setImmediate(() => {
            mainWindow.focus();
        });
    });
};
app.on('ready', createWindow);
app.on('window-all-closed', () => {
    //
    // On OS X it is common for applications and their menu bar to stay active until the user quits explicitly with Cmd + Q
    //
    if (process.platform !== 'darwin') {
        app.quit();
    }
});
app.on('activate', () => {
    //
    // On OS X it's common to re-create a window in the app when the dock icon is clicked and there are no other windows open.
    //
    if (mainWindow === null) {
        createWindow();
    }
});

/* eslint-env browser, node */
process.once('loaded', () => {
    window.nodeRequire = require;
    delete window.require;
    delete window.exports;
    delete window.module;
    global.__devtron = {require, process};
    if (process.env.NODE_ENV === 'test') {
        global.electronRequire = require;
    }
});

import {PackageJsonEditor, install} from '../utils';
import {allDoNotExist, someDoExist} from '../utils/common';
import createJsonEditor from '../utils/createJsonEditor';

const ESDOC_CONF = {
    source: './src',
    destination: './docs',
    plugins: [
        {
            name: 'esdoc-standard-plugin'
        },
        {
            name: 'esdoc-ecmascript-proposal-plugin',
            option: {
                all: true
            }
        }
    ]
};
const ESDOC_DEPENDENCIES = [
    'esdoc',
    'esdoc-ecmascript-proposal-plugin',
    'esdoc-standard-plugin'
];
const ESDOC_REACT_PLUGINS = [
    'esdoc-jsx-plugin'
];
const EsdocJsonEditor = createJsonEditor('esdoc.conf.json', ESDOC_CONF);
/**
 * @type {task[]}
 * @see https://esdoc.org/
 */
export const addEsdoc = [
    {
        text: 'Create esdoc configuration file',
        task: async () => {
            const cfg = new EsdocJsonEditor();
            await cfg.create().commit();
        },
        condition: () => allDoNotExist('esdoc.conf.json', '.esdoc.json')
    },
    {
        text: 'Add documentation tasks to package.json',
        task: async ({sourceDirectory}) => {
            const scripts = {
                'lint:docs': `eslint . --no-eslintrc --rule valid-jsdoc:error --parser babel-eslint`,
                'build:docs': `jsdoc ${sourceDirectory} -r --destination ./docs`,
                'open:docs': 'open-cli ./docs/index.html',
                preesdoc: 'npm run lint:docs',
                esdoc: 'npm run build:docs',
                postesdoc: 'npm run open:docs'
            };
            const pkg = new PackageJsonEditor();
            await pkg.extend({scripts}).commit();
        },
        condition: () => someDoExist('package.json')
    },
    {
        text: 'Install esdoc dependencies',
        task: ({skipInstall}) => install([...ESDOC_DEPENDENCIES, 'open-cli'], {dev: true, skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && someDoExist('package.json')
    },
    {
        text: 'Install esdoc React plugins',
        task: ({skipInstall}) => install(ESDOC_REACT_PLUGINS, {dev: true, skipInstall}),
        condition: ({isNotOffline, useReact}) => isNotOffline && useReact && someDoExist('package.json'),
        optional: ({useReact}) => useReact
    },
    {
        text: 'Add esdoc React plugin to configuration file',
        task: async () => {
            const {plugins} = ESDOC_CONF;
            const cfg = new EsdocJsonEditor();
            await cfg
                .extend({
                    plugins: [...plugins, {
                        name: 'esdoc-jsx-plugin',
                        options: {enable: true}
                    }]
                })
                .commit();
        },
        condition: ({useReact}) => useReact && someDoExist('esdoc.conf.json'),
        optional: ({useReact}) => useReact
    }
];
export default addEsdoc;

import {join} from 'path';
import {EslintConfigModuleEditor, PackageJsonEditor, install} from '../../utils';
import {allDoNotExist, someDoExist} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';

const ESLINT_DEPENDENCIES = [
    'eslint',
    'babel-eslint',
    'eslint-config-omaha-prime-grade',
    'watch'
];
const ESLINT_REACT_PLUGINS = [
    'eslint-plugin-react'
];
/**
 * @type {task[]}
 * @see https://eslint.org/
 */
export const tasks = [
    {
        text: 'Create ESLint configuration and .eslintignore files',
        task: async () => {
            await (new EslintConfigModuleEditor())
                .create()
                .commit();
            await (new Scaffolder(join(__dirname, 'templates')))
                .copy('.eslintignore')
                .commit();
        },
        condition: () => allDoNotExist('.eslintrc.js', '.eslintrc', '.eslintrc.json', '.eslintrc.yml')
    },
    {
        text: 'Add lint tasks to package.json',
        task: async ({sourceDirectory}) => {
            const scripts = {
                lint: `eslint . -c ./.eslintrc.js --fix`,
                'lint:watch': `watch 'npm run lint' ${sourceDirectory}`,
                'lint:tests': 'eslint __tests__/**/*.js -c ./.eslintrc.js --fix --no-ignore'
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => someDoExist('package.json')
    },
    {
        text: 'Install ESLint dependencies',
        task: ({skipInstall}) => install(ESLINT_DEPENDENCIES, {dev: true, skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && someDoExist('package.json')
    },
    {
        text: 'Install ESLint React plugins',
        task: ({skipInstall}) => install(ESLINT_REACT_PLUGINS, {dev: true, skipInstall}),
        condition: ({useReact}) => (useReact && someDoExist('package.json')),
        optional: ({isNotOffline, useReact}) => isNotOffline && useReact
    },
    {
        text: 'Add React support to ESLint configuration file',
        task: async ({reactVersion}) => {
            const REACT_BABEL_SETTINGS = {
                parserOptions: {
                    ecmaFeatures: {
                        jsx: true
                    }
                },
                settings: {
                    react: {
                        version: `'${reactVersion}'`
                    }
                },
                extends: ['omaha-prime-grade', 'plugin:react/recommended']
            };
            await (new EslintConfigModuleEditor())
                .extend(REACT_BABEL_SETTINGS)
                .commit();
        },
        condition: ({useReact}) => useReact && someDoExist('.eslintrc.js'),
        optional: ({useReact}) => useReact
    }
];
export default tasks;

import {join} from 'path';
import {PackageJsonEditor, install} from '../../utils';
import {someDoExist} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';

const ALWAYS = () => true;
const JEST_DEPENDENCIES = [
    'jest',
    'babel-jest'
];
/**
 * @type {task[]}
 * @see https://jestjs.io/
 */
export const addJest = [
    {
        text: 'Add test tasks and Jest configuration to package.json',
        task: async ({browser}) => {
            const scripts = {
                test: 'jest .*.test.js --coverage',
                'test:watch': 'npm test -- --watchAll'
            };
            const jest = {
                testMatch: ['**/__tests__/**/*.(e2e|test).[jt]s?(x)'],
                setupFilesAfterEnv: browser ? ['<rootDir>/__tests__/setup.js'] : undefined
            };
            await (new PackageJsonEditor())
                .extend({jest, scripts})
                .commit();
        },
        condition: () => someDoExist('package.json')
    },
    {
        text: 'Copy Jest boilerplate',
        task: async ({browser}) => {
            const scaffolder = new Scaffolder(join(__dirname, 'templates'));
            browser && await scaffolder
                .target('__tests__')
                .copy('setup.js');
            await scaffolder
                .target('__tests__')
                .copy('example.test.js')
                .commit();
        },
        condition: ALWAYS
    },
    {
        text: 'Install Jest dependencies',
        task: ({skipInstall}) => install(JEST_DEPENDENCIES, {dev: true, skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && someDoExist('package.json')
    }
];
export default addJest;

const delay = duration => new Promise(resolve => {
    setTimeout(resolve, duration);
});

describe('Example', () => {
    test('can pass', () => {
        expect(1).toEqual(1);
        expect(1).not.toEqual(0);
    });
    test('can pass asynchronously', async () => {
        await delay(100);
        expect(1).toEqual(1);
    });
});

class LocalStorageMock {
    constructor() {
        this.store = {};
    }
    clear() {
        this.store = {};
    }
    getItem(key) {
        return this.store[key] || null;
    }
    setItem(key, value) {
        this.store[key] = value.toString();
    }
    removeItem(key) {
        delete this.store[key];
    }
}
global.localStorage = new LocalStorageMock();

import {
    allDoExist,
    allDoExistSync,
    allDoNotExist
} from '../utils/common';
import MakefileEditor from '../utils/MakefileEditor';
/**
 * @type {task[]}
 * @see https://www.gnu.org/software/make/manual/html_node/Simple-Makefile.html#Simple-Makefile
 */
export const addMakefile = [
    {
        text: 'Create Makefile',
        task: async () => {
            await (new MakefileEditor())
                .create()
                .write('# Makefile built with tomo')
                .commit();
        },
        condition: () => allDoNotExist('Makefile')
    },
    {
        text: 'Import tasks from package.json scripts',
        task: async () => {
            await (new MakefileEditor())
                .delete()
                .create()
                .importScripts()
                .appendScripts()
                .appendHelpTask()
                .commit();
        },
        condition: () => allDoExist('Makefile', 'package.json'),
        optional: () => allDoExistSync('Makefile', 'package.json')
    }
];
export default addMakefile;

import {join} from 'path';
import {PackageJsonEditor, install} from '../../utils';
import {someDoExist} from '../../utils/common';
import {Scaffolder} from '../../utils/Scaffolder';

const MARIONETTE_DEPENDENCIES = [
    'jquery',
    'backbone',
    'backbone.marionette',
    'backbone.radio',
    'marionette.approuter',
    'morphdom',
    'lodash-es',
    'redux'
];
const ALWAYS = async () => true;
/**
 * @type {task[]}
 * @see https://marionettejs.com/
 */
export const tasks = [
    {
        text: 'Copy Marionette.js boilerplate and assets',
        task: async ({assetsDirectory, overwrite, sourceDirectory, useParcel, usePika}) => {
            const index = (useParcel || usePika) ? 'index-in-place.html' : 'index.html';
            await (new Scaffolder(join(__dirname, 'templates')))
                .overwrite(overwrite)
                .target(sourceDirectory)
                .copy('main.js')
                .target(`${sourceDirectory}/components`)
                .copy('app.js')
                .target(`${sourceDirectory}/shims`)
                .copy('mn.renderer.shim.js')
                .target(`${sourceDirectory}/plugins`)
                .copy('mn.radio.logging.js')
                .copy('mn.redux.state.js')
                .target(`${assetsDirectory}/images`)
                .copy('.gitkeep')
                .target(`${assetsDirectory}/fonts`)
                .copy('.gitkeep')
                .target(`${assetsDirectory}/library`)
                .copy('.gitkeep')
                .target(`${assetsDirectory}/workers`)
                .copy('.gitkeep')
                .commit();
            await (new Scaffolder(join(__dirname, '..', 'common', 'templates')))
                .target(`${assetsDirectory}`)
                .copy(index, 'index.html')
                .target(`${assetsDirectory}/css`)
                .copy('style.css')
                .commit();
        },
        condition: ALWAYS
    },
    {
        text: 'Set package.json "main" attribute',
        task: async ({sourceDirectory}) => {
            const main = `${sourceDirectory}/main.js`;
            await (new PackageJsonEditor())
                .extend({main})
                .commit();
        },
        condition: () => someDoExist('package.json')
    },
    {
        text: 'Install Marionette.js dependencies',
        task: ({skipInstall}) => install(MARIONETTE_DEPENDENCIES, {skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && someDoExist('package.json')
    }
];
export default tasks;

import {
    PackageJsonEditor,
    PurgecssConfigEditor,
    install,
    uninstall
} from '../utils';
import {allDoExist, allDoExistSync, allDoNotExist, someDoExist} from '../utils/common';

const BUILD_DEPENDENCIES = [
    'del-cli'
];
const PARCEL_DEPENDENCIES = [
    'parcel-bundler',
    'parcel-plugin-purgecss'
];
/**
 * @type {task[]}
 * @see https://parceljs.org/
 */
export const addParcel = [
    {
        text: 'Add Parcel build tasks to package.json',
        task: async ({assetsDirectory, outputDirectory}) => {
            const scripts = {
                'clean:build': `del-cli ${outputDirectory}`,
                prebuild: 'npm run clean:build',
                build: `parcel build --out-dir ${outputDirectory} --public-url ./ ${assetsDirectory}/index.html`,
                'prebuild:watch': 'npm run clean:build',
                'build:watch': `parcel watch --out-dir ${outputDirectory} --public-url ./ ${assetsDirectory}/index.html`,
                start: `parcel ${assetsDirectory}/index.html --out-dir ${outputDirectory} --open`
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => someDoExist('package.json')
    },
    {
        text: 'Install development dependencies and add dev task to package.json',
        task: async ({skipInstall}) => {
            const scripts = {
                dev: 'stmux [ \"npm run build:watch\" : \"npm run lint:watch\" ]'
            };
            await install(['stmux'], {dev: true, skipInstall});
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => allDoExist('package.json', '.eslintrc.js'),
        optional: () => allDoExistSync('package.json', '.eslintrc.js')
    },
    {
        text: 'Create PurgeCSS config file',
        task: async ({assetsDirectory}) => {
            const content = [`'${assetsDirectory}/index.html'`];
            await (new PurgecssConfigEditor())
                .create()
                .extend({content})
                .commit();
        },
        condition: () => allDoNotExist('purgecss.config.js')
    },
    {
        text: 'Install Parcel development dependencies',
        task: ({skipInstall}) => install([...BUILD_DEPENDENCIES, ...PARCEL_DEPENDENCIES], {dev: true, skipInstall}),
        condition: ({isNotOffline}) => isNotOffline && someDoExist('package.json')
    }
];
export const removeParcel = [
    {
        text: 'Remove Parcel build tasks from package.json',
        task: async () => {
            const scripts = {
                'clean:build': undefined,
                dev: undefined,
                prebuild: undefined,
                build: undefined,
                'prebuild:watch': undefined,
                'build:watch': undefined,
                start: undefined
            };
            await (new PackageJsonEditor())
                .extend({scripts})
                .commit();
        },
        condition: () => someDoExist('package.json')
    },
    {
        text: 'Delete PurgeCSS config file',
        task: async () => {
            await (new PurgecssConfigEditor())
                .delete()
                .commit();
        },
